mapper.select:
select userEmail, userName from
user where userEmail = #{userEmail} and
userPwd = #{userPwd}

select userEmail, userName,
userPhoneNumber from user where userEmail =
#{userEmail}

mapper.update:
update user set
userPhoneNumber = #{userPhoneNumber}
where userEmail = #{userEmail}

update user set sessionKey = #{sessionId},
sessionLimit = #{next} where
userEmail = #{userEmail}
